public class Ejerc17Tem2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i <= 10; i++){
			System.out.println("-------------------------------\n"
							+"Tabla del " +i+ "\n"
									+ "-------------------------------\n");
			for(int j = 1; j <= 10; j++){
				System.out.println(i + " x "+ j + " = " +i*j);
			}
			System.out.println();
			
		}
	}
}
